mapper.select:
SELECT a.id AS "id", a.asset_num AS "assetNum", a.`name` AS "name", a.remark AS "remark", a.qty AS "qty", b.`name` AS "status", getUserName (c.create_user) AS "createUser", DATE_FORMAT( c.create_time, "%Y-%m-%d %T" ) AS "createTime" FROM asset_register a LEFT JOIN sys_approval_list c ON a.id = c.bill_id AND c.`status` = '0', v_status b WHERE a.`status` = b.val AND a.`status`='0'
AND a.`status` =#{param.status,jdbcType=VARCHAR}
AND CONCAT(a.asset_num,a.name,a.remark) like CONCAT('%',#{param.keyword,jdbcType=VARCHAR},'%')
AND c.create_user=#{param.updateUser,jdbcType=VARCHAR}
union all
SELECT a.id AS "id", a.asset_num AS "assetNum", a.`name` AS "name", a.remark AS "remark", a.qty AS "qty", b.`name` AS "status", getUserName (c.create_user) AS "createUser", DATE_FORMAT( c.create_time, "%Y-%m-%d %T" ) AS "createTime" FROM asset_register a LEFT JOIN sys_approval_list c ON a.id = c.bill_id AND c.`status` = '0', v_status b WHERE a.`status` = b.val AND a.`status`!='0'
AND a.`status` =#{param.status,jdbcType=VARCHAR}
AND CONCAT(a.asset_num,a.name,a.remark) like CONCAT('%',#{param.keyword,jdbcType=VARCHAR},'%')
AND a.org_id = #{param.orgId,jdbcType=VARCHAR}
ORDER BY createTime desc
